import React, { Component } from 'react';
import {
    Button,
    Container,
    Menu,
    Segment,
    Visibility,
    Image,
    Popup
  } from 'semantic-ui-react';
import Login from '../Login';
import {Link} from 'react-router-dom';
import './index.css';



  class DesktopHeader extends Component {

    constructor() {
        super();
        this.state = {};    
    }
    
    hideFixedMenu = () => this.setState({ fixed: false });
    showFixedMenu = () => this.setState({ fixed: true });

    render() {
    const { fixed } = this.state;
    return (
        <Visibility
        once={false}
        onBottomPassed={this.showFixedMenu}
        onBottomPassedReverse={this.hideFixedMenu}
        >
            
            <Segment    
                inverted
                textAlign='center'
                style={{  padding: '1em 0em'}}
                vertical
                className="dekstopheader"
            >
                <Menu
                fixed={fixed ? 'top' : null}
                inverted={!fixed}
                pointing={!fixed}
                secondary={!fixed}
                size='large'
                className="dekstopheader-menu"
                >
                    <Container>
                        <Menu.Item as={Link} to='/'  >
                             <Image src={`${process.env.PUBLIC_URL}/images/dzvun-logo.png`} size="small"> </Image>
                        </Menu.Item>
                        <Menu.Item position='right'>
                        <Popup
                            trigger={
                            <Button  inverted={!fixed} className="dekstopheader-button" color="blue">
                                Log in
                            </Button>
                            }
                            content={
                                <Login/>
                            }
                            basic
                            on="click"
                            />
                            <Button as={Link} to='/signup' inverted={!fixed} primary={fixed} style={{ marginLeft: '0.5em'}} className="dekstopheader-button" color="blue">
                                Sign Up
                            </Button>
                        </Menu.Item>
                    </Container>
                </Menu>
            </Segment>
        </Visibility>
        )
    }
  }

  export default DesktopHeader;
